    private void populateTable1(){
        DefaultTableModel dtm1 = (DefaultTableModel) txttab1.getModel();
                dtm1.setRowCount(0);   
                
                
               int n =1; 
for (Professor.registercours regCourse : regcoursehistory.getRegcoursehistory()) 
{
        if (regCourse.getNEUID().equals(p_neuid))
        {
        for(Courses.CourseDetails course: regCourse.getCoursehistory()){
           if(course.getCourse_status().equals("completed"))
           {
               
         
            Object[] row = new Object[5];
            row[0] = course;
            row[1]= course.getCoursename();
            row[2] = course.getCourse_grade();
            row[3] = String.valueOf(n);
            row[4] = course.getCourse_credit();
            dtm1.addRow(row);
            n++;
        }
         
   }
}
   }
}
    
    
    
    
    private void populatefield(){
              
for (Student.Studentde stu : history.getHistory()) 
{
        if (stu.getNEUID().equals(p_neuid))
        {
            txtname.setText(stu.getName());
            txtdob.setText(stu.getSpecalization());
            txtid.setText(stu.getNEUID());
            txtjoin.setText(stu.getGradyear());
            txtaward.setText(stu.getGradyear());
            txtcode.setText("NEU");
            txtduaration.setText("2");
            txtjoin.setText(stu.getAcceptedyear());
            txtdegree.setText("Master of Science");
        }
        
        
}
     }

